"use strict";

var Lien = require("../lib");

// Init lien server
var server = new Lien({
    port: 9000,
    public: __dirname + "/public"
});

// Listen for load
server.on("load", function (err) {
    console.log(err || "Server started on port 9000.");
    err && process.exit(1);
});

// Add page
server.get("/", function (lien) {
    lien.end("Hello World");
});

// Add a dynamic route
server.get("/post/:id", function (lien) {
    lien.end("Post id: " + lien.params.id);
});

// Add a static file
server.get("/test", "index.html");

// Listen for server errors
server.on("serverError", function (err) {
    console.log(err.stack);
});